mapper.select:
SELECT R.*, W.W_NM_K, W.W_NM_E FROM REVIEW R JOIN WINE W ON R.W_NO = W.W_NO WHERE 1=1 AND RE_CONTENT LIKE CONCAT('%',#{searchKeyword},'%')
AND W_NM_K LIKE CONCAT('%',#{searchKeyword},'%') OR W_NM_E LIKE CONCAT('%',#{searchKeyword},'%')
ORDER BY RE_NO DESC LIMIT 10 OFFSET #{offset}

mapper.update:
UPDATE REVIEW SET RE_COUNT=#{re_count},
RE_PHOTO1=#{re_photo1}, RE_PHOTO2=#{re_photo2},
RE_PHOTO3=#{re_photo3}, RE_CONTENT=#{re_content} WHERE RE_NO=#{re_no}
