mapper.select:
SELECT R.*, W.W_NM_K, W.W_NM_E, W.W_IMAGE1 FROM REVIEW R JOIN WINE W ON R.W_NO = W.W_NO WHERE 1=1 AND RE_TITLE LIKE CONCAT('%',#{searchKeyword},'%') ORDER BY RE_NO DESC AND RE_CONTENT LIKE CONCAT('%',#{searchKeyword},'%') ORDER BY RE_NO DESC
AND W_NM_K LIKE CONCAT('%',#{searchKeyword},'%') OR W_NM_E LIKE CONCAT('%',#{searchKeyword},'%') ORDER BY RE_NO DESC
ORDER BY RE_NO DESC LIMIT 10 OFFSET #{offset}

mapper.update:
UPDATE REVIEW SET RE_TITLE=#{re_title}, RE_CONTENT=#{re_content}, RE_SCORE=#{re_score}, 
RE_PHOTO1=#{re_photo1}, RE_PHOTO2=#{re_photo2},	RE_PHOTO3=#{re_photo3} 
WHERE RE_NO=#{re_no}

mapper.select:
SELECT AVG(RE_SCORE)
FROM REVIEW
WHERE W_NO = #{w_no};

mapper.update:
UPDATE WINE 
SET RATING_AVG = #{rating_avg} 
WHERE W_NO = #{w_no}
